import React from "react";
import {Badge, Button, Col, Menu, Row} from "antd";

let Tags = (props) => {

    let contMap =  props.tags.map( (el)=>{
        return(
            <Menu.Item key={el.name} style={{verticalAlign: "center", display: 'inline-flex', justifyContent: 'center'}}>
                <Row style={{ display: 'inline-flex', justifyContent: 'center'}} gutter={[16, 16]} >
                    <Col >
                        <Badge count={el.upvotes} />
                    </Col>
                    <Col>
                        {el.name}
                    </Col>
                    <Col>
                        <Badge count={el.downvotes} style={{ verticalAlign: "center",  backgroundColor: '#52c41a' }}/>
                    </Col>
                </Row>
            </Menu.Item>
        )
    });
    return(
        <Menu theme="dark"  mode="inline" style={{justifyContent: 'center'}}>
            <Row style={{ color:'white', display: 'inline-flex', justifyContent: 'center'}} gutter={[16, 16]} >
                <Button>+</Button>
            </Row>
            {contMap}
        </Menu>
    )
}

export default Tags;
